<?php
require_once "../config/config.php";
require_once "../class/Livre.php";
session_start();
if (!isset($_SESSION['user_id_ad'])) {
    header("location:inserbook.php");
    exit;
}
$config_base = new config();
$bd = $config_base->getConnexion();
$livre = new Livre($bd);
$Message = '';
$alertClass = '';
if (isset($_POST['ajout'])) {
    if (
        !empty($_POST["isbn"]) && !empty($_POST["title"]) && !empty($_POST["edition"]) && !empty($_POST["author"]) && !empty($_POST["category"]) &&
        !empty($_POST["num_dispo"]) && !empty($_POST["description"])
    ) {
        $isbn = htmlspecialchars($_POST['isbn']);
        $title = htmlspecialchars($_POST['title']);
        $edition = htmlspecialchars($_POST['edition']);
        $author = htmlspecialchars($_POST['author']);
        $category = htmlspecialchars($_POST['category']);
        $num_dispo = htmlspecialchars($_POST['num_dispo']);
        $description = htmlspecialchars($_POST['description']);
        $image = htmlspecialchars($_POST['image']);

        $existingBook = $livre->getBookByISBN($isbn);
        if (!$existingBook) {
            $livre->insertBook($title, $image, $description, $author, $isbn, $category, $edition, $num_dispo);
            //$_SESSION['success'] = "insertion d'un livre avec succès";
            $Message = 'Votre insertion a été créé avec succès....';
            $alertClass = 'alert-success';
            header("Refresh:1; url=tables-books.php");
            exit;
        } else {
            $Message = '<p style="font-size:14px">Add Failed your ISBN Exist !... &nbsp; <a href="login.php" class="inline-flex items-center font-medium text-blue-600 dark:text-green-500 hover:underline">
            Update here
            <svg aria-hidden="true" class="w-5 h-5 ml-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                <path fill-rule="evenodd" d="M12.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-2.293-2.293a1 1 0 010-1.414z" clip-rule="evenodd"></path>
            </svg>
        </a><p> ';
            $alertClass = 'alert-danger';
        }
    } else {
        $Message = null;
        $alertClass = null;
    }
}



